import React, { useState } from "react";
import axios from "axios";
import "./Login.css"; // Import your custom styles

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [success, setSuccess] = useState(false);        


  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post("http://localhost:3002/api/auth/login", {
        email,
        password,
      } , { withCredentials: true });

      if (response.data.success) {
        setSuccess(true);
        // You can store the token in localStorage or cookies, if required
        // Optionally, redirect the user to the dashboard or home page
        setTimeout(() => {
          window.location.href = "/dashboard"; // Redirect to the dashboard page
        }, 2000);
      } else {
        setError(response.data.message);
      }
    } catch (error) {
      setError("Something went wrong. Please try again.");
    }
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      {error && <p className="error-message">{error}</p>}
      {success && <p className="success-message">Login successful! Redirecting...</p>}
      <form onSubmit={handleLogin}>
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          placeholder="Email"
          required
        />
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Password"
          required
        />
        <button type="submit">Log In</button>
      </form>
    </div>
  );
};

export default Login;
